CREATE TABLE `ta_rka`.`tb_fasilitas` ( `no_fasilitas` INT NOT NULL AUTO_INCREMENT , `jenis_peralatan` VARCHAR(60) NULL DEFAULT NULL , `banyaknya` INT NULL DEFAULT NULL , `anggaran` FLOAT(12,2) NULL DEFAULT NULL , `tahun` INT(4) NULL DEFAULT NULL , `status` VARCHAR(16) NULL DEFAULT NULL , `jurusan_id` INT(11) NULL DEFAULT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NULL DEFAULT NULL , `updated_by` VARCHAR(22) NULL DEFAULT NULL , PRIMARY KEY (`no_fasilitas`)) ENGINE = InnoDB;